package transporter;

public class Rematerialize {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int dog = 11;
		int cat = 3;
		int partA = dog / cat;
		int partB = dog % cat;
		int newDog = partB + partA * cat;
		System.out.println(newDog);

	}

}
